const raw = $json.output;

// Step 1: Normalize the fake JSON string (quote the keys)
const fixed = raw.replace(/([{,])\s*(\w+)\s*:/g, '$1"$2":');

let result;
try {
  result = JSON.parse(fixed);
} catch (e) {
  return [{
    json: {
      status: 500,
      answer: "Invalid response format",
      nextQ: ""
    }
  }];
}

// Step 2: Normalize missing fields
const normalized = {
  status: result.status ?? 500,
  answer: result.answer ?? "Unable to process your request.",
  nextQ: result.nextQ ?? `{{ $("ValidationFailMerge1").outputMsg }}` ?? ""
};
if(normalized){
  normalized.nextQ=result.answer+`...\r\n`+normalized.nextQ;
}
return [{ json: normalized }];
